" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"loantype\" description=\" 还款方式\" type=\"int\"/>\n      <result name=\"userId\" description=\"购买用户id\" type=\"int\"/>\n      <result name=\"dealId\" description=\"标id\" type=\"int\"/>\n      <result name=\"buyAmount\" description=\"购买商品数量\" type=\"float\"/>\n      <result name=\"money\" description=\"购买金额\" type=\"float\"/>\n      <result name=\"createTime\" description=\"投标时间\" type=\"int\"/>\n      <result name=\"dealName\" description=\"标的名称\" type=\"string\"/>\n      <result name=\"rate\" description=\"年化补偿率\" type=\"float\"/>\n      <result name=\"repayTime\" description=\"购买期限\" type=\"int\"/>\n    </handler>\n    <handler path=\"/goldbusi/get_gold_commission_detail\" version=\"1.0.0\" returnType=\"Object\">\n      <parameter name=\"cfpId\" description=\"理财师ID\" scope=\"parameter\" ref=\"cfpId\"/>\n      <parameter name=\"custIds\" description=\"客户ID数组\" scope=\"parameter\" ref=\"custIds\"/>\n      <parameter name=\"dealId\" description=\"标的ID\" scope=\"parameter\" ref=\"dealId\"/>\n      <result name=\"dealId\" description=\"标的ID\"/>\n      <result name=\"refererRebateAmount\" description=\"邀请人返点金额\"/>\n      <result name=\"refererRebateRatioAmount\" description=\"邀请人返点比例金额\"/>\n      <result name=\"payStatus\" description=\"返利结算状态\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1556447806,NULL,NULL,NULL,0,9,4,NULL,NULL);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (49,NULL,'get_repay_list','获取黄金回款列表',NULL,NULL,'1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"custIds\" description=\"客户ID数组\" type=\"int[]\" required=\"true\"/>\n    <parameter name=\"status\" description=\"回款状态\" type=\"int\" required=\"true\"/>\n    <parameter name=\"pageNo\" description=\"页编号\" type=\"int\" min=\"1\" default=\"1\"/>\n    <parameter name=\"pageSize\" description=\"页大小\" type=\"int\" min=\"1\" max=\"1000\" default=\"20\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryMoreHandler\" path=\"/base/sql_query_list\" version=\"1.0.0\" returnType=\"Array\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>gold</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT dlr.deal_id, SUM(CASE WHEN dlr.type=1 THEN dlr.money ELSE 0 END) AS principalRepayMoney, d.name AS dealName, d.repay_time AS dealRepayTime, SUM(dlr.money) AS totalRepayMoney, SUM(CASE WHEN dlr.type!=1 THEN dlr.money ELSE 0 END) AS profitRepayMoney, (CASE WHEN dlr.status=0 THEN dlr.time ELSE dlr.real_time END) AS repayTime, dlr.real_time FROM `gold_deal_loan_repay` dlr, `gold_deal` d WHERE dlr.loan_user_id IN (${custIds}) AND dlr.deal_id=d.id AND dlr.status= ${status} GROUP BY dlr.TIME,d.id ASC LIMIT ${(pageNo-1)*pageSize}, ${pageSize}</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" scope=\"parameter\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" scope=\"parameter\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"deal_id\" description=\"订单id\" type=\"int\"/>\n      <result name=\"principalRepayMoney\" description=\"本金\" type=\"float\"/>\n      <result name=\"dealName\" description=\"标的名称\" type=\"string\"/>\n      <result name=\"dealRepayTime\" description=\"标的回款时间\" type=\"int\"/>\n      <result name=\"totalRepayMoney\" description=\"总金额\" type=\"float\"/>\n      <result name=\"profitRepayMoney\" description=\"利息\" type=\"float\"/>\n      <result name=\"repayTime\" description=\"购买期限\" type=\"int\"/>\n      <result name=\"real_time\" description=\"实际回款时间\" type=\"int\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1556507095,NULL,NULL,NULL,0,9,4,NULL,NULL);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (50,NULL,'get_repay_calender_months','获取回款日历有效月份',NULL,NULL,'1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"custIds\" description=\"客户ID数组\" type=\"int[]\" required=\"true\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryMoreHandler\" path=\"/base/sql_query_list\" version=\"1.0.0\" returnType=\"Array\" parentPath=\"/tmp/b2blist\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>firstb2b</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT DISTINCT ${year()} AS repay_year, repay_month FROM firstb2b_deal_loan_repay_calendar_${year()} WHERE user_id IN (${custIds}) UNION SELECT DISTINCT ${year()+1} AS repay_year, repay_month FROM firstb2b_deal_loan_repay_calendar_${year()+1} WHERE user_id IN (${custIds}) UNION SELECT DISTINCT ${year()+2} AS repay_year, repay_month FROM firstb2b_deal_loan_repay_calendar_${year()+2} WHERE user_id IN (${custIds}) UNION SELECT DISTINCT ${year()+3} AS repay_year, repay_month FROM firstb2b_deal_loan_repay_calendar_${year()+3} WHERE user_id IN (${custIds});</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>