1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"mobile\" description=\"加密手机号\" type=\"string[]\" required=\"true\">\n      <parser class=\"com.hframework.smartweb.bean.parser.StringArraySmartParser\" pattern=\",\"/>\n    </parameter>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryOneHandler\" path=\"/base/sql_query_one\" version=\"1.0.0\" returnType=\"Object\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>firstb2b</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT usr.mobile AS mobile, image.attachment AS attachment FROM firstb2b_user AS usr INNER JOIN firstb2b_user_image image ON usr.id = image.user_id WHERE usr.mobile IN(${mobile})</value>\n      </parameter>\n      <parameter name=\"notExistsAllowed\" description=\"是否允许信息不存在\" scope=\"parameter\" default=\"false\">\n        <value>true</value>\n      </parameter>\n      <parameter name=\"notExistsMessage\" description=\"信息不存在话述\" scope=\"parameter\" default=\"对应记录不存在!\">\n        <value>对应记录不存在!</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"attachment\" description=\"文件路径\" type=\"string\" alias=\"avatar\"/>\n      <result name=\"mobile\" description=\"加密手机号\" type=\"string\"/>\n    </handler>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryOneHandler\" path=\"/base/sql_query_one\" version=\"1.0.0\" returnType=\"Object\">\n      <precheck scope=\"result\">\n        <express>$attachment == \'null\'</express>\n      </precheck>\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>firstb2b</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT bind.mobile AS mobile, info.openid, info.user_info FROM firstb2b_bonus_bind AS bind INNER JOIN firstb2b_weixin_info info ON bind.openid = info.openid WHERE bind.mobile IN(${mobile}) ORDER BY info.update_time;</value>\n      </parameter>\n      <parameter name=\"notExistsAllowed\" description=\"是否允许信息不存在\" scope=\"parameter\" default=\"false\">\n        <value>true</value>\n      </parameter>\n      <parameter name=\"notExistsMessage\" description=\"信息不存在话述\" scope=\"parameter\" default=\"对应记录不存在!\">\n        <value>对应记录不存在!</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"mobile\" description=\"加密手机号\" type=\"string\"/>\n      <result name=\"user_info\" description=\"用户的微信信息\" type=\"string\"/>\n    </handler>\n    <handler class=\"com.hframework.datacenter.handler.base.field.JsonStringParseHandler\" path=\"/base/json_parse\" version=\"1.0.0\" returnType=\"Object\">\n      <parameter name=\"jsonString\" description=\"JSON字符串\" scope=\"parameter\" ref=\"user_info\"/>\n      <parameter name=\"deleteJson\" description=\"解析后删除Json字符串\" scope=\"parameter\" default=\"true\">\n        <value>true</value>\n      </parameter>\n      <parameter name=\"returnFields\" description=\"返回值段\" default=\"${HANDLER.CONFIG.RESULTS}\">\n        <value>${HANDLER.CONFIG.RESULTS}</value>\n      </parameter>\n      <result name=\"headi