 (CASE ${orderType} WHEN 0 THEN all_invest WHEN 1 THEN b2b_commission+gold_commission WHEN 2 THEN avg_rate WHEN 3 THEN money WHEN 4 THEN last_repay_time WHEN 6 THEN avg_period WHEN 7 THEN register_time WHEN 8 THEN annual_invest WHEN 9 THEN login_time WHEN 10 THEN service_grade ELSE register_time END) AS show_str FROM lcs_user_profile WHERE cur_refer_user_id = ${cfpId} AND (CASE ${filterType} WHEN 0 THEN is_invest =1 WHEN 1 THEN idcardpassed =0 WHEN 2 THEN is_invest =0 ELSE 1=1 END)ORDER BY (CASE ${orderType} WHEN 0 THEN all_invest WHEN 1 THEN b2b_commission+gold_commission WHEN 2 THEN avg_rate WHEN 3 THEN money WHEN 4 THEN last_repay_time WHEN 6 THEN avg_period WHEN 7 THEN register_time WHEN 8 THEN annual_invest WHEN 9 THEN login_time WHEN 10 THEN service_grade ELSE register_time END) DESC LIMIT ${(pageNo-1)*pageSize}, ${pageSize};</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"register_time\" description=\"注册时间\" type=\"int\"/>\n      <result name=\"sexes\" description=\"性别\" type=\"int\"/>\n      <result name=\"real_name\" description=\"姓名\" type=\"string\" default=\"未实名\"/>\n      <result name=\"user_name\" description=\"用户名\" type=\"string\"/>\n      <result name=\"mobile\" description=\"加密手机号\" type=\"string\" alias=\"sec_mobile\"/>\n      <result name=\"user_id\" description=\"用户ID\" type=\"int\"/>\n      <result name=\"login_time\" description=\"最后活动时间\" type=\"int\"/>\n      <result name=\"all_invest\" description=\"总投资额\" type=\"float\"/>\n      <result name=\"show_str\" description=\"排序值\" type=\"float\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1557742764,NULL,NULL,NULL,0,7,4,NULL,NULL);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (72,NULL,'get_cust_count','获取客户数量',NULL,NULL,'1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"cfpId\" description=\"理财师ID\" type=\"string\" required=\"true\"/>\n    <parameter name=\"filterType\" description=\"过滤类型\" type=\"int\" default=\"-1\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryOneHandler\" path=\"/base/sql_query_one\" version=\"1.0.0\" returnType=\"Object\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>beetle</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT COUNT(*) cust_count FROM lcs_user_profile WHERE cur_refer_user_id = ${cfpId} AND (CASE ${filterType} WHEN 0 THEN is_invest =1 WHEN 1 THEN idcardpassed =0 WHEN 2 THEN is_invest =0 ELSE 1=1 END);</value>\n      </parameter>\n      <parameter name=\"notExistsAllowed\" description=\"是否允许信息不存在\" scope=\"parameter\" default=\"false\">\n        <value>true</value>\n      </parameter>\n      <parameter name=\"notExistsMessage\" description=\"信息不存在话述\" scope=\"parameter\" default=\"对应记录不存在!\">\n        <value>没有客户！</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"cust_count\" description=\"客户数量\" type=\"int\" default=\"0\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1557745191,NULL,