=\"/base/express_invoke\" version=\"1.0.0\" returnType=\"Object\">\n      <parameter name=\"express\" description=\"表达式\" scope=\"parameter\">\n        <value>${date.length() &gt;=10 ? date.substring(8,10): \'01\'}</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"day\" description=\"天\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1557822163,NULL,NULL,NULL,0,15,4,NULL,NULL);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (74,NULL,'get_offline_project_list','获取线下项目列表',NULL,NULL,'1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"cfpId\" description=\"理财师ID\" type=\"int\" required=\"true\"/>\n    <parameter name=\"cfpGroupId\" description=\"理财师分组ID\" type=\"int\" required=\"true\"/>\n    <parameter name=\"qry_offset\" description=\"查询偏移量\" type=\"int\" min=\"0\" default=\"0\"/>\n    <parameter name=\"qry_size\" description=\"查询记录数\" type=\"int\" min=\"1\" max=\"1000\" default=\"20\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryMoreHandler\" path=\"/base/sql_query_list\" version=\"1.0.0\" returnType=\"Array\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>firstb2b</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT prj.id, prj.jys_number AS name, prj.amount / 100 AS amount, IFNULL(lad.real_amount,0) / 100 AS real_amount, ROUND(IFNULL(lad.real_amount,0) / prj.amount, 4) AS real_rate, ROUND((prj.amount - IFNULL(lad.real_amount,0)) / 100, 4) AS balance_amount, prj.repay_time, (CASE WHEN prj.repay_type = 1 THEN \'天\' ELSE \'个月\' END) AS repay_time_unit, prj.repay_type, ROUND(prj.expect_year_rate / 100000,2) AS expect_year_rate, prj.min_amount / 100 AS min_amount FROM firstb2b_exchange_project prj LEFT JOIN firstb2b_exchange_project_visible_user ctl ON prj.id = ctl.pro_id AND invest_limit_type != 0 LEFT JOIN (SELECT project_id, SUM(pay_money) AS real_amount FROM firstb2b_exchange_load WHERE biz_status != 4 AND biz_status != 5 AND STATUS != 2 AND project_id IN (SELECT id FROM firstb2b_exchange_project WHERE deal_status = 2 AND is_ok = 1 AND is_visible = 1) GROUP BY project_id) lad ON prj.id = lad.project_id WHERE deal_status = 2 AND is_ok = 1 AND is_visible = 1 AND (invest_limit_type = 0 OR (invest_limit_type = 1 AND ctl.limit_id = ${cfpId} AND ctl.is_effect = 1) OR (invest_limit_type = 2 AND ctl.limit_id = ${cfpGroupId} AND ctl.is_effect = 1)) ORDER BY balance_amount &gt; 0 DESC, raise_start_time ASC LIMIT ${qry_offset}, ${qry_size}</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"id\" description=\"项目ID\" type=\"int\"/>\n      <result name=\"name\" description=\"项目名称\" type=\"string\"/>\n      <result name=\"amount\" description=\"借款金额 (元)\" type=\"float\" formatter=\"com.hframework.smartweb.bean.formatter.MoneyFormatter\" pattern=\"FormatMoneyWan\"/>\n      <result name=\"real_amount\" description=\"实际借款金额 (元)\" type=\"float\" formatter=\"com.hframework.smartweb.bean.formatter.MoneyFormatter\" pattern=\"FormatMoneyWan\"/>\n      <result name=\"real_rate\" description=\"实际借款完成比\" type=\"float\"/>\n      <result name=\"balance_amount\" description=\"剩余可借金额\" type=\"float\" alias=\"balance_amount_label\" formatter=\"com.hframework.smartweb.bean.formatter.MoneyFormatter\" pattern=\"FormatMoneyWan\"/>\n      <result name=\"balance_amount\" description=\"剩余可借金额\" type=\"float\"/>\n      <result name=\"repay_time\" description=\"产品期限数值\" type=\"int\"/>\n      <result name=\"repay_time_unit\" description=\"产品期限单位\" type=\"string\"/>\n      <result name=\"repay_type\" description=\"还款方式\" type=\"int\"/>\n      <result name=\"expect_year_rate\" description=\"预期年化收益率\" type=\"int\"/>\n      <result name=\"min_amount\" description=\"最低起投金额 (元)\" type=\"float\" formatter=\"com.hframework.smartweb.bean.formatter.MoneyFormatter\" pattern=\"FormatMoneyWan\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1558076966,1558077018,NULL,NULL,0,8,4,NULL,2);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (75,NULL,'get_offline_project_count','获取线下项目数量',NULL,NULL,'1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"cfpId\" description=\"理财师ID\" type=\"int\" required=\"true\"/>\n    <parameter name=\"cfpGroupId\" description=\"理财师分组ID\" type=\"int\" required=\"true\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryOneHandler\" path=\"/base/sql_query_one\" version=\"1.0.0\" returnType=\"Object\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>firstb2b</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT COUNT(*) as project_num FROM firstb2b_exchange_pro