>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT prj.id, prj.jys_number AS name, prj.amount / 100 AS amount, IFNULL(lad.real_amount,0)/ 100 AS real_amount, IFNULL(lad.real_amount,0) / prj.amount AS real_rate, (prj.amount - IFNULL(lad.real_amount,0)) / 100 AS balance_amount, prj.repay_time, prj.utime, (CASE WHEN prj.repay_type = 1 THEN \'天\' ELSE \'个月\' END) AS repay_time_unit, prj.repay_type, prj.expect_year_rate / 100000 AS expect_year_rate, prj.min_amount / 100 AS min_amount, invest_add_amount / 100 AS invest_add_amount, gua.name AS guarantee_name, fx.real_name AS fx_name, (CASE WHEN give_money_time = \'0000-00-00 00:00:00\' THEN NULL ELSE give_money_time END) AS give_money_time FROM firstb2b_exchange_project prj LEFT JOIN firstb2b_deal_agency gua ON gua.id = guarantee_id LEFT JOIN firstb2b_user fx ON fx.id = fx_uid LEFT JOIN (SELECT project_id, SUM(pay_money) AS real_amount FROM firstb2b_exchange_load WHERE biz_status != 4 AND biz_status != 5 AND STATUS != 2 AND project_id = ${projId}) lad ON prj.id = lad.project_id WHERE prj.id = ${projId} ;</value>\n      </parameter>\n      <parameter name=\"notExistsAllowed\" description=\"是否允许信息不存在\" scope=\"parameter\" default=\"false\">\n        <value>false</value>\n      </parameter>\n      <parameter name=\"notExistsMessage\" description=\"信息不存在话述\" scope=\"parameter\" default=\"对应记录不存在!\">\n        <value>没有找到对应的项目信息！</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"id\" description=\"项目ID\" type=\"int\"/>\n      <result name=\"name\" description=\"项目名称\" type=\"string\"/>\n      <result name=\"amount\" description=\"借款金额(元)\" type=\"float\"/>\n      <result name=\"real_amount\" description=\"实际借款金额(元)\" type=\"float\"/>\n      <result name=\"real_rate\" description=\"实际借款完成比\" type=\"float\"/>\n      <result name=\"balance_amount\" description=\"剩余可借金额(元)\" type=\"float\"/>\n      <result name=\"repay_time\" description=\"产品期限数值\" type=\"int\"/>\n      <result name=\"utime\" description=\"募集开始时间\" type=\"date\"/>\n      <result name=\"repay_time_unit\" description=\"产品期限单位\" type=\"string\"/>\n      <result name=\"repay_type\" description=\"还款方式\" type=\"int\"/>\n      <result name=\"expect_year_rate\" description=\"预期年化收益率 \" type=\"float\"/>\n      <result name=\"min_amount\" description=\"最低起投金额(元)\" type=\"float\"/>\n      <result name=\"invest_add_amount\" description=\"投资递增金额(元)\" type=\"int\"/>\n      <result name=\"guarantee_name\" description=\"担保机构名称\" type=\"string\"/>\n      <result name=\"fx_name\" description=\"融资方名称\" type=\"string\"/>\n      <result name=\"give_money_time\" description=\"项目起息时间\" type=\"date\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1558082123,NULL,NULL,NULL,0,8,4,NULL,2);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (77,NULL,'get_offline_order_list','获取线下订单列表',NULL,NULL,'1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"cfpId\" description=\"理财师ID\" type=\"int\" required=\"true\"/>\n    <parameter name=\"qryType\" description=\"查询类型\" type=\"int\" required=\"false\" default=\"0\"/>\n    <parameter name=\"projId\" description=\"项目ID\" type=\"int\" required=\"true\" default=\"-1\"/>\n    <parameter name=\"qry_offset\" description=\"查询偏移量\" type=\"int\" min=\"0\" default=\"0\"/>\n    <parameter name=\"qry_size\" description=\"查询记录数\" type=\"int\" min=\"1\" max=\"1000\" default=\"20\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryMoreHandler\" path=\"/base/sql_query_list\" version=\"1.0.0\" returnType=\"Array\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>firstb2b</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT t.id, t.real_name, t.pay_money / 100 as pay_money, t.create_time, proj.jys_number AS proj_name, (CASE WHEN STATUS =3 THEN 5 WHEN STATUS = 2 THEN 6 WHEN biz_status &lt; 5 THEN biz_status WHEN biz_status=5 THEN 4 END) AS order_status FROM firstb2b_exchange_load t LEFT JOIN firstb2b_exchange_project proj ON t.project_id = proj.id WHERE refer_user_id = ${cfpId} AND CASE WHEN ${qryType} =0 THEN 1=1 WHEN ${qryType} &lt;=4 THEN STATUS IN(1, 4) WHEN ${qryType} = 5 THEN STATUS=3 ELSE STATUS=2 END AND CASE WHEN ${qryType} =0 THEN 1=1 WHEN ${qryType} &lt;=3 THEN biz_status = ${qryType} WHEN ${qryType} =4 THEN biz_status IN(4, 5) ELSE 1=1 END AND CASE WHEN ${projId} &gt; 0 THEN t.project_id = ${projId} ELSE 1 = 1 END ORDER BY t.create_time DESC LIMIT ${qry_offset}, ${qry_size}</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"id\" description=\"订单ID\" type=\"int\"/>\n      <result name=\"real_name\" description=\"客户名称\" type=\"string\" alias=\"cust_name\"/>\n      <result name=\"pay_money\" description=\"订单金额\" type=\"int\" alias=\"total_money\"/>\n      <result name=\"create_time\" description=\"下单时间\" type=\"int\"/>\n      <result name=\"proj_name\" description=\"项目名称\" type=\"string\"/>\n      <result name=\"order_status\" description=\"订单状态\" type=\"int\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1558083385,1558085519,NULL,NULL,0,8,4,NULL,2);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (78,NULL,'get_offline_order_detail','获取离线订单详情',NULL,NULL,'1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"orderId\" description=\"订单ID\" type=\"int\" required=\"true\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryOneHandler\" path=\"/base/sql_query_one\" version=\"1.0.0\" returnType=\"Object\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>firstb2b</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT t.id, t.real_name, ROUND(t.pay_money / 100, 2) AS pay_money, t.create_time, t.project_id as proj_id, proj.jys_number AS proj_name, t.certificate_type, t.certificate_no, t.mobile, t.bank_no, t.bank_name, t.cnaps_no, t.bank_province, t.bank_city, t.bank_county, biz_status AS show_biz_status, (CASE WHEN STATUS=3 THEN 5 WHEN STATUS = 2 THEN 6 ELSE 0 END) AS show_status, (CASE WHEN STATUS=3 THEN 5 WHEN STATUS = 2 THEN 6 WHEN biz_status &lt; 5 THEN biz_status WHEN biz_status=5 THEN 4 END) AS order_status FROM firstb2b_exchange_load t LEFT JOIN firstb2b_exchange_project proj ON t.project_id = proj.id WHERE t.id = ${orderId}</value>\n      </parameter>\n      <parameter name=\"notExistsAllowed\" description=\"是否允许信息不存在\" scope=\"parameter\" default=\"false\">\n        <value>false</value>\n      </parameter>\n      <parameter name=\"notExistsMessage\" description=\"信息不存在话述\" scope=\"parameter\" default=\"对应记录不存在!\">\n        <value>没有找到对应的订单！</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"id\" description=\"订单ID\" type=\"int\"/>\n      <result name=\"real_name\" description=\"客户名称\" type=\"string\"/>\n      <result name=\"pay_money\" description=\"认购金额\" type=\"int\"/>\n      <result name=\"create_time\" description=\"下单时间\" type=\"int\"/>\n      <result name=\"proj_id\" description=\"项目id\" type=\"int\"/>\n      <result name=\"proj_name\" description=\"项目名称\" type=\"string\"/>\n      <result name=\"certificate_type\" description=\"认证类型\" type=\"string\"/>\n      <result name=\"certificate_no\" description=\"认证编号\" type=\"string\"/>\n      <result name=\"mobile\" description=\"手机号\" type=\"string\"/>\n      <result name=\"bank_no\" description=\"银行卡号\" type=\"string\"/>\n      <result name=\"bank_name\" description=\"开户行名称\" type=\"string\"/>\n      <result name=\"cnaps_no\" description=\"银联号\" type=\"string\"/>\n      <result name=\"bank_province\" description=\"开户行所在省份\" type=\"string\"/>\n      <result name=\"bank_city\" description=\"开户行所在城市\" type=\"string\"/>\n      <result name=\"bank_county\" description=\"开户行所在区\" type=\"string\"/>\n      <result name=\"show_biz_status\" description=\"审批状态\" type=\"int\"/>\n      <result name=\"show_status\" description=\"取消状态\" type=\"int\"/>\n      <result name=\"order_status\" description=\"订单状态（审批+取消）\" type=\"int\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1558088135,1558088146,NULL,NULL,0,8,4,NULL,2);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (79,NULL,'get_offline_order_actions','获取订单审批明细',NULL,NULL,'1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"orderId\" description=\"订单ID\" type=\"int\" required=\"true\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryMoreHandler\" path=\"/base/sql_query_list\" version=\"1.0.0\" returnType=\"Array\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>firstb2b</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT CASE WHEN action_type = 2 AND action_result = 1 THEN \'初审通过\' WHEN action_type = 2 AND action_result = 2 THEN \'初审失败\' WHEN action_type IN (3,4) AND action_result = 1 THEN \'复审通过\' WHEN action_type IN (3,4) AND action_result = 2 THEN \'初审失败\' WHEN action_type IN (5,6,7) THEN \'主动取消\' WHEN action_type=8 AND action_result = 1 THEN \'取消成功\' WHEN action_type=8 AND action_result = 2 THEN \'取消失败\' WHEN action_type=1 THEN \'下单成功\' ELSE \'未知\' END AS action_type, CASE WHEN action_type = 2 AND action_result = 1 THEN \'初审已通过\' WHEN action_type IN (3,4) AND action_result = 1 THEN \'复审已通过\' WHEN action_type IN (3,4) AND action_result = 2 THEN \'初审失败\' WHEN action_type=1 THEN \'等待产品起息\' ELSE remark END AS remark, create_time FROM firstb2b_exchange_action WHERE load_id = ${orderId}</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"action_type\" description=\"操作类型\" type=\"string\"/>\n      <result name=\"remark\" description=\"操作描述\" type=\"string\"/>\n      <result name=\"create_time\" description=\"创建时间\" type=\"int\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1558091617,NULL,NULL,NULL,0,8,4,NULL,2);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (80,NULL,'get_offline_order_files','获取线下订单附件列表',NULL,NULL,'1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"orderId\" description=\"订单ID\" type=\"int\" required=\"true\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryMoreHandler\" path=\"/base/sql_query_list\" version=\"1.0.0\" returnType=\"Array\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>firstb2b</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT file_type, file_path FROM firstb2b_exchange_user_file WHERE load_id = ${orderId} AND is_effect = 1</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" scope=\"parameter\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" scope=\"parameter\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"file_type\" description=\"文件类型 1:证件照片 2:银行卡照片  3:打款凭证照片\" type=\"int\"/>\n      <result name=\"file_path\" description=\"文件地址\" type=\"string\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1558091933,NULL,NULL,NULL,0,8,4,NULL,2);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (81,NULL,'get_today_offline_orders','获取今日订单概览',NULL,NULL,'1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"cfpId\" description=\"理财师ID\" type=\"string\" required=\"true\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryOneHandler\" path=\"/base/sql_query_one\" version=\"1.0.0\" returnType=\"Object\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>firstb2b</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT COUNT(*) AS order_count, SUM(pay_money) / 100 AS order_amount FROM firstb2b_exchange_load WHERE refer_user_id = ${cfpId} AND (create_time &gt;= UNIX_TIMESTAMP(CURRENT_DATE) OR update_time &gt;= UNIX_TIMESTAMP(CURRENT_DATE)) AND STATUS IN(1, 4) AND biz_status IN(1,2,3);</value>\n      </parameter>\n      <parameter name=\"notExistsAllowed\" description=\"是否允许信息不存在\" scope=\"parameter\" default=\"false\">\n        <value>false</value>\n      </parameter>\n      <parameter name=\"notExistsMessage\" description=\"信息不存在话述\" scope=\"parameter\" default=\"对应记录不存在!\">\n        <value>对应记录不存在!</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"order_count\" description=\"订单笔数\" type=\"int\"/>\n      <result name=\"order_amount\" description=\"订单总额\" type=\"float\" formatter=\"com.hframework.smartweb.bean.formatter.MoneyFormatter\" pattern=\"FormatMoneyWan\"/>\n    </handler>\n  </handlers>\n</api>\n'