\n      </parameter>\n      <result name=\"EXCHANGE_REAL_NAME\" description=\"线下交易所证件姓名开关\" type=\"int\" alias=\"exchangeRealName\" default=\"1\"/>\n      <result name=\"EXCHANGE_CERTIFICATE_TYPE\" description=\"线下交易所证件类型开关\" type=\"int\" alias=\"exchangeCertificateType\" default=\"1\"/>\n      <result name=\"EXCHANGE_CERTIFICATE_NO\" description=\"线下交易所证件号码开关\" type=\"int\" alias=\"exchangeCertificateNo\" default=\"1\"/>\n      <result name=\"EXCHANGE_MOBILE\" description=\"线下交易所手机号码开关\" type=\"int\" alias=\"exchangeMobile\" default=\"1\"/>\n      <result name=\"EXCHANGE_BANK_NAME\" description=\"线下交易所开户行全称开关\" type=\"int\" alias=\"exchangeBankName\" default=\"1\"/>\n      <result name=\"EXCHANGE_BANK_NO\" description=\"线下交易所银行卡账号开关\" type=\"int\" alias=\"exchangeBankNo\" default=\"1\"/>\n      <result name=\"EXCHANGE_CNAPS_NO\" description=\"线下交易所银联号开关\" type=\"int\" alias=\"exchangeCnapsNo\" default=\"1\"/>\n      <result name=\"EXCHANGE_BANK_PROVINCE\" description=\"线下交易所开户行所在省市开关\" type=\"int\" alias=\"exchangeBankProvince\" default=\"1\"/>\n      <result name=\"EXCHANGE_CERTIFICATE_PHOTOS\" description=\"线下交易所客户证件照片开关\" type=\"int\" alias=\"exchangeCertificatePhotos\" default=\"1\"/>\n      <result name=\"EXCHANGE_BANK_PHOTOS\" description=\"线下交易所银行卡照片开关\" type=\"int\" alias=\"exchangeBankcardPhotos\" default=\"1\"/>\n      <result name=\"EXCHANGE_PAYMENT_PHOTOS\" description=\"线下交易所打印凭证照片开关\" type=\"int\" alias=\"exchangePaymentPhotos\" default=\"1\"/>\n      <result name=\"EXCHANGE_PAY_TIME\" description=\"线下交易所线下交易所打款时间开关\" type=\"int\" alias=\"exchangePayTime\" default=\"1\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1559108467,NULL,NULL,NULL,0,6,4,NULL,2);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (86,NULL,'get_offline_push_list','获取线下订单推送列表',NULL,NULL,'v1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"cfpId\" description=\"理财师ID\" type=\"string\" required=\"true\"/>\n    <parameter name=\"qry_offset\" description=\"查询偏移量\" type=\"int\" min=\"0\" default=\"0\"/>\n    <parameter name=\"qry_size\" description=\"查询记录数\" type=\"int\" min=\"1\" max=\"1000\" default=\"20\"/>\n    <parameter name=\"qry_type\" description=\"查询类型\" type=\"int\" default=\"0\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.database.SqlQueryMoreHandler\" path=\"/base/sql_query_list\" version=\"1.0.0\" returnType=\"Array\">\n      <parameter name=\"dbKey\" description=\"数据库\" scope=\"parameter\" default=\"${DATASOURCE.DBS}\">\n        <value>beetle</value>\n      </parameter>\n      <parameter name=\"sql\" description=\"SQL语句\" scope=\"parameter\">\n        <value>SELECT *\r\nFROM lcs_message_exchange\r\nWHERE refer_user_id = ${cfpId}\r\n    AND CASE ${qry_type} WHEN 1 THEN status_remark IN(\'初审失败\',\'复审失败\')WHEN 2 THEN status_remark = \'下单成功\' WHEN 3 THEN status_remark = \'已起息\' WHEN 4 THEN status_remark = \'已取消\' ELSE 1 = 1 END\r\nORDER BY push_time DESC, invest_money DESC\r\nLIMIT ${qry_offset}, ${qry_size};</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"id\" description=\"主键\" type=\"int\"/>\n      <result name=\"project_id\" description=\"项目ID\" type=\"int\"/>\n      <result name=\"real_name\" description=\"客户姓名\" type=\"string\"/>\n      <result name=\"invest_money\" description=\"投资金额\" type=\"float\"/>\n      <result name=\"status_remark\" description=\"通知类型描述\" type=\"string\"/>\n      <result name=\"note\" description=\"通知内容\" type=\"string\"/>\n      <result name=\"push_time\" description=\"推送时间\" type=\"date\"/>\n      <result name=\"status\" description=\"通知类型\" type=\"int\"/>\n      <result name=\"load_id\" description=\"订单ID\" type=\"int\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1559727993,NULL,NULL,NULL,0,7,4,NULL,NULL);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (87,NULL,'date_compare','日期比对',NULL,NULL,'1.0.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<api>\n  <parameters>\n    <parameter name=\"date1\" description=\"日期1\" type=\"date\" required=\"true\"/>\n    <parameter name=\"data2\" description=\"日期2\" type=\"date\" required=\"true\"/>\n  </parameters>\n  <prehandler/>\n  <handlers>\n    <handler class=\"com.hframework.datacenter.handler.base.field.ExpressHandler\" path=\"/base/express_invoke\" version=\"1.0.0\" returnType=\"Object\">\n      <parameter name=\"express\" description=\"表达式\" scope=\"parameter\">\n        <value>date1 &gt; data2</value>\n      </parameter>\n      <parameter name=\"queryFields\" description=\"查询字段\" default=\"${HANDLER.CONFIG.PARAMETERS}\">\n        <value>${HANDLER.CONFIG.PARAMETERS}</value>\n      </parameter>\n      <parameter name=\"parameterValues\" description=\"查询值\" default=\"${PARAMETERS.VALUES.ARRAY}\">\n        <value>${PARAMETERS.VALUES.ARRAY}</value>\n      </parameter>\n      <result name=\"data1GreatThan2\" description=\"data1大于data2\" type=\"boolean\"/>\n    </handler>\n  </handlers>\n</api>\n',NULL,1560238545,NULL,NULL,NULL,0,15,4,NULL,NULL);insert  into `cfg_runtime_handler`(`id`,`module`,`name`,`title`,`description`,`path`,`version`,`content`,`remark`,`ctime`,`mtime`,`valid_time`,`invalid_time`,`state`,`module_id`,`program_id`,`tags`,`version_id`) values (88,NULL,'get_unrepaid_list','获取线下未回款列表',NULL,NULL,'1.0.0','<